import Axios from 'axios'
import React, { Component } from 'react'

class Post extends Component {
    constructor(){
        super()
        this.state={
            postData:"",
            postResult:""
        }
    }

    onClickHandler=(e)=>{
        Axios.post('', this.state.postData)
        .then((response) => {
            this.setState({postResult: response.data})
        }).catch((err) => {
            
        });
    }

    render() {
        return (
            <div>
                <p>{this.state.postData}</p>
                <input onChange={(e => this.setState({postData: e.target.value}))} placeholder="Enter Your Name" type="text"/>
                <button onClick={this.onClickHandler}>Send</button>
            </div>
        )
    }
}

export default  Post
